import DivergentAVClient from "./DivergentAVClient";

let dc: DivergentAVClient;

export const createDivergentAVClient = (rpcEndpoint: string) => {
  dc = new DivergentAVClient(rpcEndpoint);
  return dc;
};

export const getDivergentAVClient = (rpcEndpoint?: string) => {
  if (!dc && rpcEndpoint) createDivergentAVClient(rpcEndpoint);
  return dc;
};
