import DivergentClient from "./DivergentClient";

let dc: DivergentClient;

export const createDivergentClient = (rpcEndpoint: string) => {
  dc = new DivergentClient(rpcEndpoint);
  return dc;
};

export const getDivergentClient = (rpcEndpoint?: string) => {
  if (!dc && rpcEndpoint) createDivergentClient(rpcEndpoint);
  return dc;
};
