import { IError, IErrorType, StoreSlice } from "./storeTypes";

const createErrorSlice: StoreSlice<IError> = (set) => ({
  errorType: null,
  errorMessage: undefined,
  setError: (type: IErrorType, msg: string) => {
    set(() => ({ errorType: type, errorMessage: msg }));
    setTimeout(() => {
      set(() => ({ errorType: null, errorMessage: undefined }));
    }, 5000);
  },
});

export default createErrorSlice;
